import React, { useState } from "react"
import { Card, Alert } from "react-bootstrap"
import { useAuth } from "../contexts/AuthContext"
import { Link, useHistory } from "react-router-dom"
import SimpleBottomNavigation from "./Bottom"
import { auth } from '../firebase';

import { useAuthState } from 'react-firebase-hooks/auth';
import {generateDID, genKeyPair} from '../utilities';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';

import { green } from '@mui/material/colors';
import AssignmentIcon from '@mui/icons-material/Assignment';

import Box from '@mui/material/Box';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormHelperText from '@mui/material/FormHelperText';
import Checkbox from '@mui/material/Checkbox';




export default function Dashboard() {

  const [user] = useAuthState(auth);
  const [error, setError] = useState("")
  const { currentUser, logout } = useAuth()
  const history = useHistory()
  const [page, setPage] = useState(0)

  const [state, setState] = React.useState({
    dob: true,
    licence_no: false,
    expiry: false,
  });

  const [state2, setState2] = React.useState({
    wam: true,
    faculty: false,
    units: false,
  });


  const handleChange = (event) => {
    setState({
      ...state,
      [event.target.name]: event.target.checked,
    });
  };

  const handleChange2 = (event) => {
    setState2({
      ...state2,
      [event.target.name]: event.target.checked,
    });
  };


  const { dob, licence_no, expiry } = state;
  const { wam, faculty, units } = state2;

  async function handleLogout() {
    setError("")

    try {
      await logout()
      history.push("/login")
    } catch {
      setError("Failed to log out")
    }
  }
  let a = genKeyPair()
  return (
    <>
    { page === 0 && <Box>
      <Card>
        <Card.Body>
          <h2 className="text-center mb-4">Profile</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <p><strong>Email:</strong> {currentUser.email}</p>
          <p><strong>DID:</strong> {generateDID(user.uid)}</p>
          <p><strong>private key:</strong> {a}</p>

          <Link to="/update-profile" className="btn btn-primary w-100 mt-3">
            Update Profile
          </Link>
        </Card.Body>
      </Card>
      <div className="w-100 text-center mt-2">
        <Button variant="link" onClick={handleLogout}>
          Log Out
        </Button>
      </div>
    </Box>
    }
    { page === 1 && 
      <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
        <ListItem alignItems="flex-start">
          <ListItemAvatar>
            <Avatar sx={{ bgcolor: green[500] }}>
              <AssignmentIcon onClick={() => setPage(3)}/>
            </Avatar>
          </ListItemAvatar>
          <ListItemText
            primary="Driver's license"
            secondary={
              <React.Fragment>
                <Typography
                  sx={{ display: 'inline' }}
                  component="span"
                  variant="body2"
                  color="text.primary"
                >
                  Issued by: Transport for NSW
                </Typography>
              </React.Fragment>
            }
          />
        </ListItem>
        <Divider variant="inset" component="li" />
        <ListItem alignItems="flex-start">
          <ListItemAvatar>
            <Avatar sx={{ bgcolor: green[500] }}>
              <AssignmentIcon />
            </Avatar>
          </ListItemAvatar>
          <ListItemText
            primary="Transcript"
            secondary={
              <React.Fragment>
                <Typography
                  sx={{ display: 'inline' }}
                  component="span"
                  variant="body2"
                  color="text.primary"
                >
                  Issued by: UNSW
                </Typography>
              </React.Fragment>
            }
          />
        </ListItem>
      </List>
    }
    {
      page === 3 && <Box>
         <FormControl sx={{ m: 3 }} component="fieldset" variant="standard">
        <FormLabel component="legend">Selective Disclosure</FormLabel>
        <FormGroup>
          <FormControlLabel
            control={
              <Checkbox checked={dob} onChange={handleChange} name="dob" />
            }
            label="DOB"
          />
          <FormControlLabel
            control={
              <Checkbox checked={licence_no} onChange={handleChange} name="licence_no" />
            }
            label="Licence NO"
          />
          <FormControlLabel
            control={
              <Checkbox checked={expiry} onChange={handleChange} name="expiry" />
            }
            label="Expiry"
          />
        </FormGroup>
        <FormHelperText>Please choose the fields you want to disclose</FormHelperText>
      </FormControl>
      </Box>
    }
    {
      page === 4 && <Box>
         <FormControl sx={{ m: 3 }} component="fieldset" variant="standard">
        <FormLabel component="legend">Selective Disclosure</FormLabel>
        <FormGroup>
          <FormControlLabel
            control={
              <Checkbox checked={wam} onChange={handleChange2} name="dob" />
            }
            label="DOB"
          />
          <FormControlLabel
            control={
              <Checkbox checked={jason} onChange={handleChange2} name="licence_no" />
            }
            label="Licence NO"
          />
          <FormControlLabel
            control={
              <Checkbox checked={antoine} onChange={handleChange2} name="expiry" />
            }
            label="Expiry"
          />
        </FormGroup>
        <FormHelperText>Please choose the fields you want to disclose</FormHelperText>
      </FormControl>
      </Box>
    }
    <SimpleBottomNavigation setPage ={setPage}/>
  </>
  )
}
